        """Test 404 response for non-existent user."""
        self.client.get('/user/123') \
            .expect(404, done)

    def test_user_edit_form_display(self, done: Callable) -> None:
        """Test displaying user edit form."""
        self.client.get('/user/1/edit') \
            .expect('/Guillermo/') \
            .expect(200, '/<form/', done)

    def test_user_update_error_handling(self, done: Callable) -> None:
        """Test error handling during user update."""
        self.client.put('/user/1') \
            .send({}) \
            .expect(500, done)

    def test_user_update_success(self, done: Callable) -> None:
