class MvcAcceptanceTests:
    """Main test orchestrator for MVC application acceptance tests."""

    def __init__(self, app: Any):
        self.mvc_suite = MvcTestSuite(app)
        self.user_suite = UserTestSuite(app)

    def run_redirect_tests(self) -> None:
        """Execute tests for redirect functionality."""
        def done(): pass
        self.mvc_suite.test_root_redirect(done)

    def run_pet_tests(self) -> None:
        """Execute all pet-related tests."""
        def done(): pass
        self.mvc_suite.test_pet_display(done)
        self.mvc_suite.test_pet_edit_page(done)
        self.mvc_suite.test_pet_update(done)

    def run_user_tests(self) -> None:
        """Execute all user-related tests."""
        def done(): pass
        self.user_suite.test_users_list_display(done)
        self.user_suite.test_existing_user_display(done)
        self.user_suite.test_user_pets_display(done)
        self.user_suite.test_nonexistent_user_404(done)
        self.user_suite.test_user_edit_form_display(done)
        self.user_suite.test_user_update_error_handling(done)
        self.user_suite.test_user_update_success(done)
        self.user_suite.test_pet_creation_for_user(done)


# Test execution functions maintaining original structure
def test_mvc_redirect(app: Any) -> None:
    """Test MVC redirect functionality."""
    tests = MvcAcceptanceTests(app)
    tests.run_redirect_tests()


def test_mvc_pets(app: Any) -> None:
    """Test MVC pet functionality."""
    tests = MvcAcceptanceTests(app)
    tests.run_pet_tests()


def test_mvc_users(app: Any) -> None:
    """Test MVC user functionality."""
    tests = MvcAcceptanceTests(app)
    tests.run_user_tests()

