"""
Express view module for template rendering.
Copyright(c) 2009-2013 TJ Holowaychuk
Copyright(c) 2013 Roman Shtylman
Copyright(c) 2014-2015 Douglas Christopher Wilson
MIT Licensed
"""

import os
import importlib
from typing import Dict, List, Optional, Callable, Any, Union
from pathlib import Path


class TemplateEngineLoader:
    """Responsible for loading and managing template engines."""

    def __init__(self, engines: Dict[str, Callable]):
        self._engines = engines

    def get_engine(self, extension: str) -> Callable:
        """Get or load a template engine for the given extension."""
        if extension not in self._engines:
            self._engines[extension] = self._load_engine(extension)
        return self._engines[extension]

    def _load_engine(self, extension: str) -> Callable:
        """Load a template engine module."""
        module_name = extension.lstrip('.')
        try:
            module = importlib.import_module(module_name)
            engine_function = getattr(module, '__express', None)

            if not callable(engine_function):
                raise ValueError(f'Module "{module_name}" does not provide a view engine.')

            return engine_function
        except ImportError as e:
            raise ImportError(f'Could not import template engine "{module_name}": {e}')


class FileResolver:
    """Handles file path resolution and validation."""

    @staticmethod
    def resolve_template_path(directory: str, filename: str, extension: str) -> Optional[str]:
        """Resolve template file path with fallback to index file."""
        # Try direct file path
        direct_path = os.path.join(directory, filename)
        if FileResolver._is_valid_file(direct_path):
            return direct_path

        # Try index file fallback
        base_name = FileResolver._get_base_name(filename, extension)
        index_path = os.path.join(directory, base_name, f'index{extension}')
        if FileResolver._is_valid_file(index_path):
            return index_path

        return None

    @staticmethod
    def _is_valid_file(file_path: str) -> bool:
        """Check if path exists and is a file."""
        try:
            return os.path.isfile(file_path)
        except OSError:
            return False

    @staticmethod
    def _get_base_name(filename: str, extension: str) -> str:
        """Get base name without extension."""
        return os.path.splitext(filename)[0] if filename.endswith(extension) else filename


class PathLookup:
    """Handles template path lookup across multiple root directories."""

    def __init__(self, roots: Union[str, List[str]]):
        self.roots = [roots] if isinstance(roots, str) else list(roots)

    def find_template(self, template_name: str, extension: str) -> Optional[str]:
        """Find template file across all root directories."""
        for root in self.roots:
            template_path = self._search_in_root(root, template_name, extension)
            if template_path:
                return template_path
        return None

    def _search_in_root(self, root: str, template_name: str, extension: str) -> Optional[str]:
        """Search for template in a specific root directory."""
        absolute_path = os.path.abspath(os.path.join(root, template_name))
        directory = os.path.dirname(absolute_path)
        filename = os.path.basename(absolute_path)

        return FileResolver.resolve_template_path(directory, filename, extension)


class ViewConfiguration:
    """Manages view configuration and validation."""

    def __init__(self, template_name: str, options: Dict[str, Any]):
        self.template_name = template_name
        self.default_engine = options.get('defaultEngine')
        self.engines = options.get('engines', {})
        self.root = options.get('root')

        self.extension = self._determine_extension()
        self.filename = self._build_filename()

        self._validate_configuration()

    def _determine_extension(self) -> str:
        """Determine the template file extension."""
        file_extension = os.path.splitext(self.template_name)[1]

        if file_extension:
            return file_extension

        if self.default_engine:
            return f'.{self.default_engine}' if not self.default_engine.startswith('.') else self.default_engine

        raise ValueError('No default engine was specified and no extension was provided.')

    def _build_filename(self) -> str:
        """Build the complete filename with extension."""
        if os.path.splitext(self.template_name)[1]:
            return self.template_name
        return self.template_name + self.extension

    def _validate_configuration(self) -> None:
        """Validate the view configuration."""
        if not self.extension and not self.default_engine:
            raise ValueError('No default engine was specified and no extension was provided.')


class AsyncRenderer:
    """Handles asynchronous template rendering."""

    def __init__(self, engine: Callable, template_path: str):
        self.engine = engine
        self.template_path = template_path

    def render(self, options: Dict[str, Any], callback: Callable) -> None:
        """Render template with options and handle sync/async callback normalization."""
        is_sync = True

        def render_callback(*args, **kwargs):
            if not is_sync:
                return callback(*args, **kwargs)

            # Force async execution for sync callbacks
            self._schedule_async_callback(callback, args, kwargs)

        self.engine(self.template_path, options, render_callback)
        is_sync = False

    def _schedule_async_callback(self, callback: Callable, args: tuple, kwargs: dict) -> None:
        """Schedule callback for next tick to ensure async behavior."""
        import threading
        timer = threading.Timer(0, lambda: callback(*args, **kwargs))
        timer.start()


class View:
    """
    Template view class for rendering templates with various engines.

    Handles template path resolution, engine loading, and rendering.
    """

    def __init__(self, name: str, options: Optional[Dict[str, Any]] = None):
        """
        Initialize a new View with the given name and options.

        Args:
            name: Template name
            options: Configuration options including defaultEngine, engines, root
        """
        options = options or {}

        self.config = ViewConfiguration(name, options)
        self.engine_loader = TemplateEngineLoader(self.config.engines)
        self.path_lookup = PathLookup(self.config.root)

        self.name = name
        self.extension = self.config.extension
        self.default_engine = self.config.default_engine
        self.root = self.config.root

        self.engine = self.engine_loader.get_engine(self.extension)
        self.path = self._lookup_template_path()

    def _lookup_template_path(self) -> Optional[str]:
        """Lookup template path using the configured root directories."""
        return self.path_lookup.find_template(self.config.filename, self.extension)

    def render(self, options: Dict[str, Any], callback: Callable) -> None:
        """
        Render the template with given options.

        Args:
            options: Template rendering options
            callback: Callback function to handle render result
        """
        if not self.path:
            callback(ValueError(f'Template "{self.name}" not found'))
            return

        renderer = AsyncRenderer(self.engine, self.path)
        renderer.render(options, callback)


# Export the main View class
__all__ = ['View']

