        return self._engines[extension]

    def _load_engine(self, extension: str) -> Callable:
        """Load a template engine module."""
        module_name = extension.lstrip('.')
        try:
            module = importlib.import_module(module_name)
            engine_function = getattr(module, '__express', None)

            if not callable(engine_function):
                raise ValueError(f'Module "{module_name}" does not provide a view engine.')

            return engine_function
        except ImportError as e:
            raise ImportError(f'Could not import template engine "{module_name}": {e}')


