        if FileResolver._is_valid_file(direct_path):
            return direct_path

        # Try index file fallback
        base_name = FileResolver._get_base_name(filename, extension)
        index_path = os.path.join(directory, base_name, f'index{extension}')
        if FileResolver._is_valid_file(index_path):
            return index_path

        return None

