class AsyncRenderer:
    """Handles asynchronous template rendering."""

    def __init__(self, engine: Callable, template_path: str):
        self.engine = engine
        self.template_path = template_path

    def render(self, options: Dict[str, Any], callback: Callable) -> None:
        """Render template with options and handle sync/async callback normalization."""
        is_sync = True

        def render_callback(*args, **kwargs):
            if not is_sync:
