class SecurityValidator:
    """Handles security validation for file paths and template names."""

    # Pattern to detect directory traversal attempts
    TRAVERSAL_PATTERN = re.compile(r'\.{2}[/\\]|[/\\]\.{2}|^\.{2}$')

    # Pattern for valid template names (alphanumeric, dots, hyphens, underscores, forward slashes)
    VALID_NAME_PATTERN = re.compile(r'^[a-zA-Z0-9._/-]+$')

    # Pattern for valid file extensions
    VALID_EXT_PATTERN = re.compile(r'^\.[a-zA-Z0-9]+$')

    @staticmethod
    def validate_template_name(name: str) -> str:
        """Validate and sanitize template name."""
        if not isinstance(name, str):
            raise ValueError("Template name must be a string")

        if not name or len(name) > 255:
            raise ValueError("Template name must be between 1 and 255 characters")

        # Check for directory traversal
        if SecurityValidator.TRAVERSAL_PATTERN.search(name):
            raise ValueError("Template name contains invalid path traversal sequences")

        # Validate character set
        if not SecurityValidator.VALID_NAME_PATTERN.match(name):
            raise ValueError("Template name contains invalid characters")

        return name.strip()

    @staticmethod
    def validate_extension(extension: str) -> str:
        """Validate file extension."""
        if not isinstance(extension, str):
            raise ValueError("Extension must be a string")

        if not extension.startswith('.'):
            extension = '.' + extension

        if not SecurityValidator.VALID_EXT_PATTERN.match(extension) or len(extension) > 10:
            raise ValueError("Invalid file extension")

        return extension

    @staticmethod
    def validate_path(file_path: str, allowed_roots: List[str]) -> str:
        """Validate that file path is within allowed roots."""
        if not isinstance(file_path, str):
            raise ValueError("File path must be a string")

        try:
            resolved_path = os.path.realpath(file_path)
        except (OSError, ValueError):
            raise ValueError("Invalid file path")

        # Check if path is within any allowed root
        for root in allowed_roots:
            try:
                resolved_root = os.path.realpath(root)
                if resolved_path.startswith(resolved_root + os.sep) or resolved_path == resolved_root:
                    return resolved_path
            except (OSError, ValueError):
                continue

        raise ValueError("File path is outside allowed directories")

    @staticmethod
    def sanitize_options(options: Dict[str, Any]) -> Dict[str, Any]:
        """Sanitize template options to prevent XSS."""
        if not isinstance(options, dict):
            return {}

        sanitized = {}
        for key, value in options.items():
            if isinstance(key, str) and len(key) <= 100:
                sanitized_key = re.sub(r'[^a-zA-Z0-9_-]', '', key)
                if sanitized_key:
                    if isinstance(value, str):
                        # HTML escape string values to prevent XSS
                        sanitized[sanitized_key] = html.escape(value, quote=True)
                    elif isinstance(value, (int, float, bool)):
                        sanitized[sanitized_key] = value
                    elif isinstance(value, (list, dict)):
                        # Recursively sanitize nested structures
                        sanitized[sanitized_key] = SecurityValidator._sanitize_nested(value)

        return sanitized

    @staticmethod
    def _sanitize_nested(value: Union[List, Dict]) -> Union[List, Dict]:
        """Recursively sanitize nested data structures."""
        if isinstance(value, dict):
            return SecurityValidator.sanitize_options(value)
        elif isinstance(value, list):
            return [SecurityValidator._sanitize_nested(item) if isinstance(item, (list, dict)) 
                   else html.escape(str(item), quote=True) if isinstance(item, str) 
                   else item for item in value if isinstance(item, (str, int, float, bool, list, dict))]
