"""
requests.compat
~~~~~~~~~~~~~~~

This module previously handled import compatibility issues
between Python 2 and Python 3. It remains for backwards
compatibility until the next major version.
"""

import importlib
import re
import sys
import html
import os
from typing import Any, Optional, Type, Union, Tuple, List
from types import ModuleType

# -------
# urllib3
# -------
from urllib3 import __version__ as urllib3_version


class VersionParser:
    """Handles version string parsing and validation."""

    VERSION_PATTERN: re.Pattern[str] = re.compile(r'^[0-9]+(\.[0-9]+)*$')

    @classmethod
    def is_valid_version_string(cls, version: Any) -> bool:
        """Check if version is a valid string format."""
        if not isinstance(version, str):
            return False

        # Sanitize input and limit length
        sanitized_version = html.escape(version.strip())[:50]
        return cls.VERSION_PATTERN.match(sanitized_version) is not None

    @classmethod
    def parse_major_version(cls, version: str) -> int:
        """Extract major version number from version string."""
        if not isinstance(version, str):
            raise ValueError("Version must be a string")

        # Sanitize and validate input
        sanitized_version = html.escape(version.strip())
        if not cls.is_valid_version_string(sanitized_version):
            raise ValueError("Invalid version format")

        version_parts: List[str] = sanitized_version.split(".")
        if version_parts and version_parts[0].isdigit():
            major_version = int(version_parts[0])
            if 0 <= major_version <= 999:  # Reasonable bounds check
                return major_version
        raise ValueError("Invalid version format")


class Urllib3VersionChecker:
    """Determines urllib3 version compatibility."""

    def __init__(self, parser: VersionParser = None) -> None:
        self._parser = parser or VersionParser()

    def is_version_one(self, version: Any) -> bool:
        """Determine if urllib3 version is 1.x."""
        try:
            return self._check_version_safely(version)
        except (TypeError, AttributeError, ValueError, IndexError):
            # If we can't discern a version, prefer old functionality.
            return True

    def _check_version_safely(self, version: Any) -> bool:
        """Parse version string with validation."""
        if not self._parser.is_valid_version_string(version):
            return True

        major_version = self._parser.parse_major_version(version)
        return major_version == 1


# Detect which major version of urllib3 is being used.
_urllib3_checker = Urllib3VersionChecker()
is_urllib3_1: bool = _urllib3_checker.is_version_one(urllib3_version)

# -------------------
# Character Detection
# -------------------


class LibraryValidator:
    """Validates library names for safe imports."""

    LIBRARY_NAME_PATTERN: re.Pattern[str] = re.compile(r'^[a-zA-Z_][a-zA-Z0-9_]{0,63}$')
    ALLOWED_LIBRARIES: frozenset = frozenset(["chardet", "charset_normalizer", "simplejson", "json"])

    @classmethod
    def is_valid_library_name(cls, library_name: str) -> bool:
        """Validate library name to prevent arbitrary imports."""
        if not isinstance(library_name, str):
            return False

        # Sanitize input
        sanitized_name = html.escape(library_name.strip())

        # Check against whitelist and pattern
        return (sanitized_name in cls.ALLOWED_LIBRARIES and 
                cls.LIBRARY_NAME_PATTERN.match(sanitized_name) is not None)


class LibraryImporter:
    """Safely imports libraries with error handling."""

    def __init__(self, validator: LibraryValidator = None) -> None:
        self._validator = validator or LibraryValidator()

    def try_import(self, library_name: str) -> Optional[ModuleType]:
        """Attempt to import a single library with validation."""
        if not self._validator.is_valid_library_name(library_name):
            return None

        try:
            # Additional security check - ensure library_name hasn't been tampered with
            sanitized_name = html.escape(library_name.strip())
            return importlib.import_module(sanitized_name)
        except (ImportError, ValueError, AttributeError, OSError, RuntimeError):
            return None


class CharacterDetectionResolver:
    """Resolves available character detection libraries."""

    SUPPORTED_LIBRARIES: Tuple[str, ...] = ("chardet", "charset_normalizer")

    def __init__(self, importer: LibraryImporter = None) -> None:
        self._importer = importer or LibraryImporter()

    def resolve(self) -> Optional[ModuleType]:
        """Find supported character detection libraries."""
        for library_name in self.SUPPORTED_LIBRARIES:
            library_module = self._importer.try_import(library_name)
            if library_module is not None:
                return library_module
        return None


chardet: Optional[ModuleType] = CharacterDetectionResolver().resolve()

