class PythonVersionChecker:
    """Provides Python version information and compatibility checks."""

    def __init__(self, version_info: Tuple[int, ...] = sys.version_info) -> None:
        if not isinstance(version_info, tuple) or len(version_info) < 2:
            raise ValueError("Invalid version_info format")

        # Validate version numbers are reasonable
        if not all(isinstance(v, int) and 0 <= v <= 999 for v in version_info[:2]):
            raise ValueError("Invalid version numbers")

        self._version_info: Tuple[int, ...] = version_info

    @property
    def is_python_2(self) -> bool:
        """Check if running Python 2.x."""
        return self._version_info[0] == 2

    @property
    def is_python_3(self) -> bool:
        """Check if running Python 3.x."""
        return self._version_info[0] == 3


