class JsonLibraryImporter:
    """Handles JSON library import resolution."""

    @staticmethod
    def resolve_json_module() -> Tuple[ModuleType, bool]:
        """Resolve json/simplejson module import."""
        # Check environment variable for JSON preference (secure configuration)
        prefer_builtin = os.getenv('REQUESTS_USE_BUILTIN_JSON', '').lower() == 'true'

        if not prefer_builtin:
            try:
                import simplejson as json
                return json, True
            except ImportError:
                pass

        import json
        return json, False


class JsonDecodeErrorProvider:
    """Provides appropriate JSONDecodeError class."""

    @staticmethod
    def get_decode_error_class(has_simplejson: bool) -> Type[Exception]:
        """Get appropriate JSONDecodeError class."""
        try:
            if has_simplejson:
                from simplejson import JSONDecodeError
                return JSONDecodeError
            else:
                from json import JSONDecodeError
                return JSONDecodeError
        except ImportError:
            # Fallback to ValueError for compatibility
            return ValueError


