"""
requests.compat
~~~~~~~~~~~~~~~

This module previously handled import compatibility issues
between Python 2 and Python 3. It remains for backwards
compatibility until the next major version.
"""

import importlib
import re
import sys
import html
import os
from typing import Any, Optional, Type, Union, Tuple, List
from types import ModuleType

# -------
# urllib3
# -------
from urllib3 import __version__ as urllib3_version


class VersionParser:
    """Handles version string parsing and validation."""

    VERSION_PATTERN: re.Pattern[str] = re.compile(r'^[0-9]+(\.[0-9]+)*$')
    MAX_VERSION_LENGTH: int = 50
    MAX_VERSION_NUMBER: int = 999

    @classmethod
    def is_valid_version_string(cls, version: Any) -> bool:
        """Check if version is a valid string format."""
        if not isinstance(version, str):
            return False

        # Input validation - check length before processing
        if len(version) > cls.MAX_VERSION_LENGTH:
            return False

        # Sanitize input against XSS and limit length
        try:
            sanitized_version = html.escape(version.strip(), quote=True)[:cls.MAX_VERSION_LENGTH]
        except (TypeError, AttributeError):
            return False

        if not sanitized_version:
            return False

        return cls.VERSION_PATTERN.match(sanitized_version) is not None

    @classmethod
    def parse_major_version(cls, version: str) -> int:
        """Extract major version number from version string."""
        if not isinstance(version, str):
            raise ValueError("Version must be a string")

        # Input validation - check length
        if len(version) > cls.MAX_VERSION_LENGTH:
            raise ValueError("Version string too long")

        # Sanitize and validate input against XSS
        try:
            sanitized_version = html.escape(version.strip(), quote=True)
        except (TypeError, AttributeError):
            raise ValueError("Invalid version format")

        if not sanitized_version:
            raise ValueError("Empty version string")

        if not cls.is_valid_version_string(sanitized_version):
            raise ValueError("Invalid version format")

        version_parts: List[str] = sanitized_version.split(".")
        if version_parts and version_parts[0].isdigit():
            try:
                major_version = int(version_parts[0])
                if 0 <= major_version <= cls.MAX_VERSION_NUMBER:  # Bounds validation
                    return major_version
            except (ValueError, OverflowError):
                pass
        raise ValueError("Invalid version format")


class Urllib3VersionChecker:
    """Determines urllib3 version compatibility."""

    def __init__(self, parser: VersionParser = None) -> None:
        self._parser = parser or VersionParser()

    def is_version_one(self, version: Any) -> bool:
        """Determine if urllib3 version is 1.x."""
        try:
            return self._check_version_safely(version)
        except (TypeError, AttributeError, ValueError, IndexError, OverflowError):
            # If we can't discern a version, prefer old functionality.
            return True

    def _check_version_safely(self, version: Any) -> bool:
        """Parse version string with validation."""
        if not self._parser.is_valid_version_string(version):
            return True

        major_version = self._parser.parse_major_version(version)
        return major_version == 1


