class PythonVersionChecker:
    """Provides Python version information and compatibility checks."""

    MAX_VERSION_NUMBER: int = 999

    def __init__(self, version_info: Tuple[int, ...] = sys.version_info) -> None:
        if not isinstance(version_info, tuple) or len(version_info) < 2:
            raise ValueError("Invalid version_info format")

        # Validate version numbers are reasonable and secure
        try:
            if not all(isinstance(v, int) and 0 <= v <= self.MAX_VERSION_NUMBER for v in version_info[:2]):
                raise ValueError("Invalid version numbers")
        except (TypeError, OverflowError):
            raise ValueError("Invalid version numbers")

        self._version_info: Tuple[int, ...] = version_info

    @property
    def is_python_2(self) -> bool:
        """Check if running Python 2.x."""
        return self._version_info[0] == 2

    @property
    def is_python_3(self) -> bool:
        """Check if running Python 3.x."""
        return self._version_info[0] == 3


# Syntax sugar.
_python_version_checker: PythonVersionChecker = PythonVersionChecker()
is_py2: bool = _python_version_checker.is_python_2
is_py3: bool = _python_version_checker.is_python_3


class JsonLibraryImporter:
    """Handles JSON library import resolution."""

    @staticmethod
    def resolve_json_module() -> Tuple[ModuleType, bool]:
        """Resolve json/simplejson module import."""
        # Check environment variable for JSON preference (secure configuration)
        env_value = os.getenv('REQUESTS_USE_BUILTIN_JSON', '')

        # Input validation for environment variable
        if not isinstance(env_value, str) or len(env_value) > 10:
            prefer_builtin = False
        else:
            # Sanitize environment input
            try:
                sanitized_env = html.escape(env_value.strip().lower(), quote=True)
                prefer_builtin = sanitized_env == 'true'
            except (TypeError, AttributeError):
                prefer_builtin = False

        if not prefer_builtin:
            try:
                import simplejson as json
                return json, True
            except (ImportError, AttributeError, OSError):
                pass

        import json
        return json, False


class JsonDecodeErrorProvider:
    """Provides appropriate JSONDecodeError class."""

    @staticmethod
    def get_decode_error_class(has_simplejson: bool) -> Type[Exception]:
        """Get appropriate JSONDecodeError class."""
        if not isinstance(has_simplejson, bool):
            return ValueError

        try:
            if has_simplejson:
                from simplejson import JSONDecodeError
                return JSONDecodeError
            else:
                from json import JSONDecodeError
                return JSONDecodeError
        except (ImportError, AttributeError):
            # Fallback to ValueError for compatibility
            return ValueError


class JsonLibraryResolver:
    """Resolves JSON library and provides access to components."""

    def __init__(self, 
                 importer: JsonLibraryImporter = None,
                 error_provider: JsonDecodeErrorProvider = None) -> None:
        self._importer = importer or JsonLibraryImporter()
        self._error_provider = error_provider or JsonDecodeErrorProvider()
        self._json_module: Optional[ModuleType] = None
        self._has_simplejson: bool = False
        self._initialize()

    def _initialize(self) -> None:
        """Initialize JSON library resolution."""
        try:
            self._json_module, self._has_simplejson = self._importer.resolve_json_module()
        except (ImportError, AttributeError, OSError, TypeError, ValueError):
            # Fallback to standard json module
            try:
                import json
                self._json_module = json
                self._has_simplejson = False
            except (ImportError, AttributeError):
                # Final fallback - set to None if json unavailable
                self._json_module = None
                self._has_simplejson = False

    @property
    def has_simplejson(self) -> bool:
        """Check if simplejson is available."""
        return self._has_simplejson

    @property
    def json_decode_error_class(self) -> Type[Exception]:
        """Get appropriate JSONDecodeError class."""
        return self._error_provider.get_decode_error_class(self._has_simplejson)


