#!/usr/bin/env python

"""
requests.certs
~~~~~~~~~~~~~~

This module returns the preferred default CA certificate bundle. There is
only one — the one from the certifi package.

If you are packaging Requests, e.g., for a Linux distribution or a managed
environment, you can change the definition of where() to return a separately
packaged CA bundle.
"""
import os
import sys
from typing import List, Optional
from certifi import where


class CertificatePathValidator:
    """Validates certificate bundle paths for security and accessibility."""

    DANGEROUS_PATTERNS: List[str] = ['..', '~', '$', '`', ';', '|', '&', '<', '>']

    def validate_path_format(self, cert_path: str) -> None:
        """Validate the certificate path format and security."""
        if not cert_path or not isinstance(cert_path, str):
            raise ValueError("Invalid certificate bundle path")

        normalized_path = os.path.normpath(cert_path)

        if not os.path.isabs(normalized_path):
            raise ValueError("Certificate path must be absolute")

        self._check_for_dangerous_patterns(normalized_path)

    def _check_for_dangerous_patterns(self, cert_path: str) -> None:
        """Check if path contains dangerous character sequences."""
        if any(pattern in cert_path for pattern in self.DANGEROUS_PATTERNS):
            raise ValueError("Certificate path contains invalid characters")

    def validate_file_accessibility(self, cert_path: str) -> None:
        """Validate that the certificate file exists and is readable."""
        if not os.path.isfile(cert_path):
            raise ValueError("Certificate bundle file does not exist")

        if not os.access(cert_path, os.R_OK):
            raise ValueError("Certificate bundle is not readable")


class CertificatePathProvider:
    """Provides and validates certificate bundle paths."""

    def __init__(self, validator: Optional[CertificatePathValidator] = None):
        self._validator = validator or CertificatePathValidator()

    def get_certificate_path(self) -> str:
        """Get and validate the certificate bundle path."""
        try:
            cert_path = where()
            self._validator.validate_path_format(cert_path)

            normalized_path = os.path.normpath(cert_path)
            self._validator.validate_file_accessibility(normalized_path)

            return normalized_path

        except Exception as e:
            raise RuntimeError("Unable to locate certificate bundle") from e


class OutputSanitizer:
    """Sanitizes output to prevent injection attacks."""

    @staticmethod
    def sanitize_path_output(path: str) -> str:
        """Remove potentially dangerous characters from path output."""
        return path.replace('\n', '').replace('\r', '')


class CertificateApplication:
    """Main application for certificate path retrieval."""

    def __init__(
        self, 
        provider: Optional[CertificatePathProvider] = None,
        sanitizer: Optional[OutputSanitizer] = None
    ):
        self._provider = provider or CertificatePathProvider()
        self._sanitizer = sanitizer or OutputSanitizer()

    def run(self) -> None:
        """Execute the main certificate path retrieval process."""
        try:
            certificate_path = self._provider.get_certificate_path()
            safe_path = self._sanitizer.sanitize_path_output(certificate_path)
            print(safe_path)
        except (ValueError, RuntimeError):
            sys.stderr.write("Error: Unable to locate certificate bundle\n")
            sys.exit(1)


def get_certificate_path() -> str:
    """Get and validate the certificate bundle path."""
    provider = CertificatePathProvider()
    return provider.get_certificate_path()


def main() -> None:
    """Main entry point for certificate path retrieval."""
    app = CertificateApplication()
    app.run()


if __name__ == "__main__":
    main()

