"""

Demonstrates Rich tracebacks for recursion errors.

Rich can exclude frames in the middle to avoid huge tracebacks.

"""

from typing import Any
from rich.console import Console


class RecursionDemonstrator:
    """Demonstrates recursive error handling with Rich console output."""

    def __init__(self, console: Console) -> None:
        self._console = console

    def _function_a(self, value: Any) -> Any:
        """First function in recursive chain."""
        return self._function_b(value)

    def _function_b(self, value: Any) -> Any:
        """Second function in recursive chain."""
        return self._function_a(value)

    def demonstrate_recursive_error(self, initial_value: Any, max_frames: int = 20) -> None:
        """Demonstrates recursive error with limited traceback frames."""
        try:
            self._function_a(initial_value)
        except Exception:
            self._console.print_exception(max_frames=max_frames)


def main() -> None:
    """Main function to demonstrate recursive error handling."""
    console = Console()
    demonstrator = RecursionDemonstrator(console)
    demonstrator.demonstrate_recursive_error(1)


if __name__ == "__main__":
    main()

