class MockApp:
    def __init__(self) -> None:
        self.middleware: list[Callable[[Any, Any], None]] = []

    def use(self, handler: Callable[[Any, Any], None]) -> None:
        self.middleware.append(handler)


class MockRequest:
    def __init__(self) -> None:
        pass


class MockResponse:
    def __init__(self) -> None:
        self.status_code: Optional[int] = None
        self.ended: bool = False

    def status(self, code: Union[int, float, str, None]) -> 'MockResponse':
        if code is None or code is ...:  # undefined equivalent
            raise ValueError("Invalid status code")

        if isinstance(code, str):
            raise ValueError("Invalid status code")

        if code != code:  # NaN check
            raise ValueError("Invalid status code")

        if not isinstance(code, (int, float)):
            raise ValueError("Invalid status code")

        if isinstance(code, float) and code != int(code):
            raise ValueError("Invalid status code")

        code_int = int(code) if isinstance(code, float) else code

        if code_int < 100 or code_int > 999:
            raise ValueError("Invalid status code")

        self.status_code = code_int
        return self

    def end(self) -> None:
        self.ended = True


