from typing import Dict, List, Optional, Callable, Any, Union
from unittest import TestCase
import asyncio
from dataclasses import dataclass

@dataclass
class Request:
    params: Dict[str, str]
    method: str
    path: str

@dataclass  
class Response:
    status_code: int = 200
    body: str = ""

    def send(self, data: str) -> None:
        self.body = data

    def status(self, code: int) -> None:
        self.status_code = code

class Route:
    def __init__(self, path: str) -> None:
        self.path: str = path
        self.handlers: Dict[str, List[Callable]] = {}

    def get(self, handler: Callable[[Request, Response], Optional[Any]]) -> 'Route':
        if 'GET' not in self.handlers:
            self.handlers['GET'] = []
        self.handlers['GET'].append(handler)
        return self

    def post(self, handler: Callable[[Request, Response], Optional[Any]]) -> 'Route':
        if 'POST' not in self.handlers:
            self.handlers['POST'] = []
        self.handlers['POST'].append(handler)
        return self

    def all(self, handler: Callable[[Union[Request, Exception], Response, Optional[Callable]], Optional[Any]]) -> 'Route':
        if 'ALL' not in self.handlers:
            self.handlers['ALL'] = []
        self.handlers['ALL'].append(handler)
        return self

class Express:
    def __init__(self) -> None:
        self.routes: List[Route] = []

    def route(self, path: str) -> Route:
        route: Route = Route(path)
        self.routes.append(route)
        return route

class RequestTest:
    def __init__(self, app: Express) -> None:
        self.app: Express = app
        self.method: str = "GET"
        self.path: str = "/"

    def post(self, path: str) -> 'RequestTest':
        self.method = "POST"
        self.path = path
        return self

    def get(self, path: str) -> 'RequestTest':
        self.method = "GET" 
        self.path = path
        return self

    def expect(self, expected: Union[str, int], callback: Optional[Callable[[], None]] = None) -> None:
        if callback:
            callback()

def request(app: Express) -> RequestTest:
    return RequestTest(app)

def express() -> Express:
    return Express()

class AppRouteTest(TestCase):

    def test_should_return_a_new_route(self) -> None:
        def done() -> None:
            pass

        app: Express = express()

        def get_handler(req: Request, res: Response) -> None:
            res.send('get')

        def post_handler(req: Request, res: Response) -> None:
            res.send('post')

        app.route('/foo').get(get_handler).post(post_handler)

        request(app).post('/foo').expect('post', done)

    def test_should_all_verb_after_all(self) -> None:
        def done() -> None:
            pass

        app: Express = express()

        def all_handler(req: Request, res: Response, next: Callable[[], None]) -> None:
            next()

        def get_handler(req: Request, res: Response) -> None:
            res.send('get')

        def post_handler(req: Request, res: Response) -> None:
            res.send('post')

        app.route('/foo').all(all_handler).get(get_handler).post(post_handler)

        request(app).post('/foo').expect('post', done)

    def test_should_support_dynamic_routes(self) -> None:
        def done() -> None:
            pass

        app: Express = express()

        def get_handler(req: Request, res: Response) -> None:
            res.send(req.params['foo'])

        app.route('/:foo').get(get_handler)

        request(app).get('/test').expect('test', done)

    def test_should_not_error_on_empty_routes(self) -> None:
        def done() -> None:
            pass

        app: Express = express()

        app.route('/:foo')

        request(app).get('/test').expect(404, done)

    def test_should_pass_rejected_promise_value(self) -> None:
        def done() -> None:
            pass

        app: Express = express()
        route: Route = app.route('/foo')

        async def create_error(req: Request, res: Response, next: Callable[[Exception], None]) -> None:
            raise Exception('boom!')

        def hello_world(req: Request, res: Response) -> None:
            res.send('hello, world!')

        def handle_error(err: Exception, req: Request, res: Response, next: Callable[[], None]) -> None:
            res.status(500)
            res.send('caught: ' + str(err))

        route.all(create_error)
        route.all(hello_world)  
        route.all(handle_error)

        request(app).get('/foo').expect(500, done)

