        route: Route = app.route('/foo')

        async def create_error(req: Request, res: Response, next: Callable[[Exception], None]) -> None:
            raise Exception('Rejected promise')

        def hello_world(req: Request, res: Response) -> None:
            res.send('hello, world!')

        def handle_error(err: Exception, req: Request, res: Response, next: Callable[[], None]) -> None:
            res.status(500)
