class User:
    def __init__(self, name: str, user_id: int, pets: List[Pet] = None) -> None:
        self.name = name
        self.id = user_id
        self.pets = pets or []

    def to_dict(self) -> Dict[str, Any]:
        return {
            "name": self.name,
            "id": self.id,
            "pets": [pet.to_dict() for pet in self.pets]
        }

class Database:
    def __init__(self) -> None:
        self._pets: List[Pet] = []
        self._users: List[User] = []
        self._initialize_data()

    @property
    def pets(self) -> List[Dict[str, Any]]:
        return [pet.to_dict() for pet in self._pets]

    @property
    def users(self) -> List[Dict[str, Any]]:
        return [user.to_dict() for user in self._users]

    def _initialize_data(self) -> None:
        self._create_pets()
        self._create_users()

    def _create_pets(self) -> None:
        pet_data = [
            ("Tobi", 0),
            ("Loki", 1),
            ("Jane", 2),
            ("Raul", 3)
        ]

        for name, pet_id in pet_data:
            self._pets.append(Pet(name, pet_id))

    def _create_users(self) -> None:
        user_configs = [
