"""
requests.hooks
~~~~~~~~~~~~~~

This module provides the capabilities for the Requests hooks system.

Available hooks:

``response``:
    The response generated from a Request.
"""
from typing import Any, Callable, Dict, List, Optional, Union


HOOKS = ["response"]


def default_hooks() -> Dict[str, List[Any]]:
    """Create default hooks dictionary with empty lists for each hook type."""
    return {event: [] for event in HOOKS}


def _is_valid_key(key: Any) -> bool:
    """Validate that the key is a valid string with allowed characters."""
    if key is None or not isinstance(key, str):
        return False
    return key.replace('_', '').replace('-', '').isalnum()


def _is_valid_hooks_dict(hooks: Any) -> bool:
    """Validate that hooks is a dictionary."""
    return isinstance(hooks, dict)


def _is_callable_hook(hook: Any) -> bool:
    """Check if a hook is callable."""
    return hasattr(hook, "__call__")


def _is_iterable_collection(hooks: Any) -> bool:
    """Check if hooks collection is iterable."""
    try:
        iter(hooks)
        return True
    except TypeError:
        return False


def _normalize_hooks_to_list(hooks: Union[Callable, List[Callable]]) -> List[Callable]:
    """Convert single callable to list or return existing list."""
    if _is_callable_hook(hooks):
        return [hooks]
    return hooks if _is_iterable_collection(hooks) else []


def _execute_single_hook(hook: Callable, hook_data: Any, **kwargs) -> Optional[Any]:
    """Execute a single hook safely, returning None on error."""
    try:
        return hook(hook_data, **kwargs)
    except Exception:
        # Prevent hook execution errors from breaking the flow
        return None


def _execute_hook_collection(hooks: List[Callable], hook_data: Any, **kwargs) -> Any:
    """Execute all hooks in sequence, updating hook_data with results."""
    current_data = hook_data

    for hook in hooks:
        if not _is_callable_hook(hook):
            continue

        result = _execute_single_hook(hook, current_data, **kwargs)
        if result is not None:
            current_data = result

    return current_data


def dispatch_hook(key: Any, hooks: Optional[Dict[str, Any]], hook_data: Any, **kwargs) -> Any:
    """
    Dispatches a hook dictionary on a given piece of data.

    Args:
        key: The hook key to dispatch
        hooks: Dictionary of hooks organized by key
        hook_data: Data to pass to the hooks
        **kwargs: Additional keyword arguments for hooks

    Returns:
        The potentially modified hook_data after processing
    """
    if not _is_valid_key(key):
        return hook_data

    hooks_dict = hooks or {}
    if not _is_valid_hooks_dict(hooks_dict):
        return hook_data

    hook_collection = hooks_dict.get(key)
    if not hook_collection:
        return hook_data

    normalized_hooks = _normalize_hooks_to_list(hook_collection)
    return _execute_hook_collection(normalized_hooks, hook_data, **kwargs)

