            if not (100 <= status_or_pattern <= 999):
                raise ValueError("Invalid expected status code")
        else:
            raise ValueError("Invalid status or pattern type")

        if isinstance(pattern_or_done, str):
            # Sanitize pattern strings
            pattern_or_done = html.escape(pattern_or_done)

        req = MockRequest()
        res = MockResponse()

        try:
            for handler in self.app.middleware:
                if callable(handler):
                    handler(req, res)

            if isinstance(status_or_pattern, int):
                expected_status = status_or_pattern
                expected_pattern = pattern_or_done if isinstance(pattern_or_done, str) else None
                done_callback = done if done else (pattern_or_done if callable(pattern_or_done) else None)
            else:
                expected_status = 500
                expected_pattern = status_or_pattern
                done_callback = pattern_or_done if callable(pattern_or_done) else done

            assert res.status_code == expected_status

            if done_callback and callable(done_callback):
                done_callback()

        except Exception as e:
            error_msg = html.escape(str(e))  # Sanitize error messages
            if "Invalid status code" in error_msg:
                assert status_or_pattern == 500
                if pattern_or_done and "Invalid status code" in str(pattern_or_done):
                    if done and callable(done):
                        done()
            else:
                raise


