"""

Demonstrates the use of multiple Progress instances in a single Live display.    

"""

from time import sleep
from typing import List
from dataclasses import dataclass

from rich.live import Live
from rich.panel import Panel
from rich.progress import Progress, SpinnerColumn, BarColumn, TextColumn, TaskID
from rich.table import Table


@dataclass
class JobConfig:
    description: str
    total: int


class ProgressManager:
    def __init__(self, job_configs: List[JobConfig]) -> None:
        self._job_progress = self._create_job_progress()
        self._job_tasks = self._create_job_tasks(job_configs)
        self._overall_progress = self._create_overall_progress()

    def _create_job_progress(self) -> Progress:
        return Progress(
            "{task.description}",
            SpinnerColumn(),
            BarColumn(),
            TextColumn("[progress.percentage]{task.percentage:>3.0f}%"),
        )

    def _create_job_tasks(self, job_configs: List[JobConfig]) -> List[TaskID]:
        tasks = []
        for config in job_configs:
            task_id = self._job_progress.add_task(config.description, total=config.total)
            tasks.append(task_id)
        return tasks

    def _create_overall_progress(self) -> Progress:
        total_work = sum(task.total for task in self._job_progress.tasks)
        overall_progress = Progress()
        overall_progress.add_task("All Jobs", total=int(total_work))
        return overall_progress

    @property
    def job_progress(self) -> Progress:
        return self._job_progress

    @property
    def overall_progress(self) -> Progress:
        return self._overall_progress

    def advance_all_active_jobs(self) -> None:
        for task in self._job_progress.tasks:
            if not task.finished:
                self._job_progress.advance(task.id)

    def update_overall_progress(self) -> None:
        total_completed = sum(task.completed for task in self._job_progress.tasks)
        overall_task_id = self._overall_progress.tasks[0].id
        self._overall_progress.update(overall_task_id, completed=total_completed)

    def is_finished(self) -> bool:
        return self._overall_progress.finished


class DisplayBuilder:
    @staticmethod
    def create_progress_table(progress_manager: ProgressManager) -> Table:
        progress_table = Table.grid()
        progress_table.add_row(
            Panel.fit(
                progress_manager.overall_progress, 
                title="Overall Progress", 
                border_style="green", 
                padding=(2, 2)
            ),
            Panel.fit(
                progress_manager.job_progress, 
                title="[b]Jobs", 
                border_style="red", 
                padding=(1, 2)
            ),
        )
        return progress_table


def run_progress_demo() -> None:
    job_configs = [
        JobConfig("[green]Cooking", 100),
        JobConfig("[magenta]Baking", 200),
        JobConfig("[cyan]Mixing", 400),
    ]

    progress_manager = ProgressManager(job_configs)
    progress_table = DisplayBuilder.create_progress_table(progress_manager)

    with Live(progress_table, refresh_per_second=10):
        while not progress_manager.is_finished():
            sleep(0.1)
            progress_manager.advance_all_active_jobs()
            progress_manager.update_overall_progress()


if __name__ == "__main__":
    run_progress_demo()

