from __future__ import annotations

import typing as t
from threading import local

if t.TYPE_CHECKING:
    from .core import Context

_thread_local_storage: local = local()


class ContextStack:
    """Manages a thread-local stack of Click contexts."""

    def __init__(self, storage: local) -> None:
        self._storage = storage

    def get_current(self) -> Context | None:
        """Get the current context from the top of the stack."""
        try:
            return t.cast("Context", self._storage.stack[-1])
        except (AttributeError, IndexError):
            return None

    def push(self, context: Context) -> None:
        """Push a context onto the stack."""
        self._storage.__dict__.setdefault("stack", []).append(context)

    def pop(self) -> None:
        """Remove the top context from the stack."""
        self._storage.stack.pop()


_context_stack = ContextStack(_thread_local_storage)


@t.overload
def get_current_context(silent: t.Literal[False] = False) -> Context: ...


@t.overload
def get_current_context(silent: bool = ...) -> Context | None: ...


def get_current_context(silent: bool = False) -> Context | None:
    """Returns the current click context.  This can be used as a way to
    access the current context object from anywhere.  This is a more implicit
    alternative to the :func:`pass_context` decorator.  This function is
    primarily useful for helpers such as :func:`echo` which might be
    interested in changing its behavior based on the current context.

    To push the current context, :meth:`Context.scope` can be used.

    .. versionadded:: 5.0

    :param silent: if set to `True` the return value is `None` if no context
                   is available.  The default behavior is to raise a
                   :exc:`RuntimeError`.
    """
    current_context = _context_stack.get_current()

    if current_context is None and not silent:
        raise RuntimeError("There is no active click context.")

    return current_context


def push_context(ctx: Context) -> None:
    """Pushes a new context to the current stack."""
    _context_stack.push(ctx)


def pop_context() -> None:
    """Removes the top level from the stack."""
    _context_stack.pop()


def resolve_color_default(color: bool | None = None) -> bool | None:
    """Internal helper to get the default value of the color flag.  If a
    value is passed it's returned unchanged, otherwise it's looked up from
    the current context.
    """
    if color is not None:
        return color

    current_context: Context | None = get_current_context(silent=True)

    if current_context is not None:
        return current_context.color

    return None

