        'REQUESTS_LICENSE': 'Apache-2.0',
        'REQUESTS_COPYRIGHT': 'Copyright Kenneth Reitz',
        'REQUESTS_CAKE': '\u2728 \U0001f370 \u2728',
    }


class EnvironmentReader:
