    def validate(self, value: str) -> Union[str, int]:
        """Validate and return the processed value."""
        ...


class StringInputChecker:
    """Validates that input values are strings."""

    @staticmethod
    def check(value: str, validator_name: str) -> None:
        """Ensure the input value is a string."""
        if not isinstance(value, str):
            raise TypeError(f"{validator_name} requires string input")


class StringCleaner:
    """Cleans and sanitizes string values."""

    _DANGEROUS_CHARS = re.compile(r'[<>"\';\\]')
    _MAX_LENGTH = 1000

    def clean(self, value: str) -> str:
        """Remove dangerous characters and limit length."""
        safe_value = self._DANGEROUS_CHARS.sub('', value)
        return safe_value[:self._MAX_LENGTH]


class BaseValidator(ABC):
    """Base validator with common string checking."""

    def __init__(self) -> None:
        self._checker = StringInputChecker()

    @abstractmethod
    def validate(self, value: str) -> Union[str, int]:
        """Validate and return the processed value."""
        pass

    def _check_string(self, value: str) -> None:
        """Validate input is a string."""
        self._checker.check(value, self.__class__.__name__)


class StringValidator(BaseValidator):
    """Validates and cleans string input."""

    def __init__(self) -> None:
        super().__init__()
        self._cleaner = StringCleaner()

    def validate(self, value: str) -> str:
        """Clean string input."""
        self._check_string(value)
        return self._cleaner.clean(value)


class VersionValidator(BaseValidator):
    """Validates semantic version format."""

    _PATTERN = re.compile(r'^\d+\.\d+\.\d+$')

    def validate(self, value: str) -> str:
        """Validate semantic version format (x.y.z)."""
        self._check_string(value)
        if not self._PATTERN.match(value):
            raise ValueError(f"Invalid version format: {value}")
        return value


class PatternValidator(BaseValidator):
    """Base validator for regex pattern matching."""

    def __init__(self, pattern: re.Pattern[str], string_validator: StringValidator) -> None:
        super().__init__()
        self._pattern = pattern
        self._string_validator = string_validator

    def validate(self, value: str) -> str:
        """Validate pattern and clean string."""
        self._check_string(value)
        if not self._pattern.match(value):
            raise ValueError(f"Invalid format: {value}")
        return self._string_validator.validate(value)


class UrlValidator(PatternValidator):
    """Validates URL format."""

    _PATTERN = re.compile(r'^https?://[a-zA-Z0-9.-]+[a-zA-Z0-9]')

    def __init__(self, string_validator: StringValidator) -> None:
        super().__init__(self._PATTERN, string_validator)


