from typing import Callable, Optional, Any, Dict
import os
import sys

class Request:
    def __init__(self) -> None:
        pass

class Response:
    def send(self, message: str) -> None:
        print(message)

class App:
    def __init__(self) -> None:
        self.middleware: list[Callable[[Request, Response, Optional[Callable[[], None]]], None]] = []
        self.final_handler: Optional[Callable[[Request, Response], None]] = None

    def use(self, handler: Callable[[Request, Response, Optional[Callable[[], None]]], None]) -> None:
        if len([param for param in handler.__code__.co_varnames[:handler.__code__.co_argcount]]) == 2:
            self.final_handler = handler
        else:
            self.middleware.append(handler)

    def listen(self, port: int) -> None:
        print(f"Server listening on port {port}")

        req: Request = Request()
        res: Response = Response()

        def run_middleware(index: int = 0) -> None:
            if index < len(self.middleware):
                def next_func() -> None:
                    run_middleware(index + 1)
                self.middleware[index](req, res, next_func)
            elif self.final_handler:
                self.final_handler(req, res)

        run_middleware()

def express() -> App:
    return App()

app: App = express()

n: int = int(os.environ.get('MW', '1'))
print(f'  {n} middleware')

while n > 0:
    n -= 1
    def middleware_handler(req: Request, res: Response, next: Optional[Callable[[], None]]) -> None:
        if next:
            next()
    app.use(middleware_handler)

def final_handler(req: Request, res: Response) -> None:
    res.send('Hello World')

app.use(final_handler)
app.listen(3333)

