class ColorDisplayService:
    """Service for displaying colored text demonstrations."""

    @staticmethod
    def demonstrate_colors() -> None:
        """Demonstrates ANSI color support."""
        colors = ("red", "green", "blue")
        for color in colors:
            click.echo(click.style(f"I am colored {color}", fg=color))
            click.echo(click.style(f"I am background colored {color}", bg=color))


class PagerService:
    """Service for displaying paginated content."""

    @staticmethod
    def display_numbered_lines(line_count: int = 200) -> None:
        """Displays numbered lines using the pager."""
        lines = []
        for x in range(line_count):
            lines.append(f"{click.style(str(x), fg='green')}. Hello World!")
        click.echo_via_pager("\n".join(lines))


class ProgressBarService:
    """Service for displaying various types of progress bars."""

    @staticmethod
    def _process_item_slowly() -> None:
        """Simulates slow processing with random delay."""
        time.sleep(0.002 * random.random())

    @staticmethod
    def _filter_items(items: range) -> Iterator[int]:
        """Filters items randomly (70% pass rate)."""
        for item in items:
            if random.random() > 0.3:
                yield item

    @staticmethod
    def _show_item(item: Optional[int]) -> Optional[str]:
        """Formats item for display in progress bar."""
        if item is not None:
            return f"Item #{item}"
        return None

    def demonstrate_progress_bars(self, count: int) -> None:
        """Demonstrates various progress bar configurations."""
        items = range(count)

        self._show_basic_progress_bar(items)
        self._show_filtered_progress_bar(items)
        self._show_custom_template_progress_bar(count)
        self._show_minimal_progress_bar(count)
        self._show_non_linear_progress_bar()

    def _show_basic_progress_bar(self, items: range) -> None:
        """Shows basic progress bar with green fill."""
        with click.progressbar(
            items, 
            label="Processing accounts", 
            fill_char=click.style("#", fg="green")
        ) as bar:
            for item in bar:
                self._process_item_slowly()

    def _show_filtered_progress_bar(self, items: range) -> None:
        """Shows progress bar with filtered items and custom display."""
        with click.progressbar(
            self._filter_items(items),
            label="Committing transaction",
            fill_char=click.style("#", fg="yellow"),
            item_show_func=self._show_item,
        ) as bar:
            for item in bar:
                self._process_item_slowly()

    def _show_custom_template_progress_bar(self, count: int) -> None:
        """Shows progress bar with custom template."""
        with click.progressbar(
            length=count,
            label="Counting",
            bar_template="%(label)s  %(bar)s | %(info)s",
            fill_char=click.style("█", fg="cyan"),
            empty_char=" ",
        ) as bar:
            for item in bar:
                self._process_item_slowly()

    def _show_minimal_progress_bar(self, count: int) -> None:
        """Shows minimal progress bar without percentage and ETA."""
        with click.progressbar(
            length=count,
            width=0,
            show_percent=False,
            show_eta=False,
            fill_char=click.style("#", fg="magenta"),
        ) as bar:
            for item in bar:
                self._process_item_slowly()

    def _show_non_linear_progress_bar(self) -> None:
        """Shows progress bar with non-linear progression."""
        steps = [math.exp(x * 1.0 / 20) - 1 for x in range(20)]
        count = int(sum(steps))
        with click.progressbar(
            length=count,
            show_percent=False,
            label="Slowing progress bar",
            fill_char=click.style("█", fg="green"),
        ) as bar:
            for item in steps:
                time.sleep(item)
                bar.update(item)


class LaunchService:
    """Service for launching files and URLs."""

    @staticmethod
    def open_url(url: str) -> None:
        """Opens a file or URL in the default application."""
        click.launch(url)

    @staticmethod
    def locate_url(url: str) -> None:
        """Opens a file or URL in the file manager."""
        click.launch(url, locate=True)


class EditorService:
    """Service for text editing operations."""

    MARKER = "# Everything below is ignored\n"

    def open_editor(self) -> None:
        """Opens an editor with some text in it."""
        message = click.edit(f"\n\n{self.MARKER}")
        if message is not None:
            self._process_editor_message(message)
        else:
            click.echo("You did not enter anything!")

    def _process_editor_message(self, message: str) -> None:
        """Processes the message from the editor."""
        msg = message.split(self.MARKER, 1)[0].rstrip("\n")
        if not msg:
            click.echo("Empty message!")
        else:
            click.echo(f"Message:\n{msg}")


class MenuService:
    """Service for handling interactive menus."""

    def __init__(self):
        self._current_menu = "main"
        self._menu_handlers = {
            "main": self._handle_main_menu,
            "debug": self._handle_debug_menu,
            "quit": self._handle_quit_menu
        }

    def run_interactive_menu(self) -> None:
        """Shows and handles an interactive menu system."""
        while self._current_menu != "quit":
            handler = self._menu_handlers.get(self._current_menu)
            if handler:
                handler()
            else:
                break

    def _handle_main_menu(self) -> None:
        """Handles main menu interactions."""
        click.echo("Main menu:")
        click.echo("  d: debug menu")
        click.echo("  q: quit")
        char = click.getchar()
        if char == "d":
            self._current_menu = "debug"
        elif char == "q":
            self._current_menu = "quit"
        else:
            click.echo("Invalid input")

    def _handle_debug_menu(self) -> None:
        """Handles debug menu interactions."""
        click.echo("Debug menu")
        click.echo("  b: back")
        char = click.getchar()
        if char == "b":
            self._current_menu = "main"
        else:
            click.echo("Invalid input")

    def _handle_quit_menu(self) -> None:
        """Handles quit menu state."""
        pass


