from typing import Callable, Dict, Any, Optional, Protocol
import unittest
from unittest.mock import Mock
import re


class Request(Protocol):
    pass


class Response(Protocol):
    def send(self, content: str) -> None: ...
    def shout(self, string: str) -> None: ...


class App(Protocol):
    response: Dict[str, Any]
    def use(self, path: str, app: 'App') -> None: ...
    def get(self, path: str, handler: Callable[[Request, Response], None]) -> None: ...


class TestClient(Protocol):
    def get(self, path: str) -> 'TestResponse': ...


class TestResponse(Protocol):
    def expect(
        self, 
        status_or_content: Any, 
        content_or_callback: Optional[Any] = None, 
        callback: Optional[Callable[[], None]] = None
    ) -> 'TestResponse': ...


class CallbackCounter:
    """Handles counting calls and executing callback after specified count"""

    def __init__(self, target_count: int, callback: Callable[[], None]) -> None:
        self._target_count = target_count
        self._callback = callback
        self._current_count = 0

    def __call__(self) -> None:
        self._current_count += 1
        if self._current_count >= self._target_count:
            self._callback()


class ResponseExtensionFactory:
    """Factory for creating response extensions"""

    @staticmethod
    def create_shout_uppercase() -> Callable[[Response, str], None]:
        """Creates a shout method that converts to uppercase"""
        def shout(response: Response, text: str) -> None:
            response.send(text.upper())
        return shout

    @staticmethod
    def create_shout_exclamation() -> Callable[[Response, str], None]:
        """Creates a shout method that adds exclamation mark"""
        def shout(response: Response, text: str) -> None:
            response.send(text + '!')
        return shout


class RequestHandlerFactory:
    """Factory for creating request handlers"""

    @staticmethod
    def create_shout_handler(text: str) -> Callable[[Request, Response], None]:
        """Creates a handler that uses response.shout with given text"""
        def handler(request: Request, response: Response) -> None:
            response.shout(text)
        return handler


class AppFactory:
    """Factory for creating app instances"""

    @staticmethod
    def create_express_app() -> App:
        """Creates a mock express app"""
        app_mock = Mock()
        app_mock.response = {}
        return app_mock


class TestClientFactory:
    """Factory for creating test clients"""

    @staticmethod
    def create_test_client(app: App) -> TestClient:
        """Creates a test client for the given app"""
        return Mock()


def after(count: int, callback: Callable[[], None]) -> Callable[[], None]:
    """Creates a callback counter that executes callback after count calls"""
    return CallbackCounter(count, callback)


def express() -> App:
    """Creates an express app instance"""
    return AppFactory.create_express_app()


def request(app: App) -> TestClient:
    """Creates a test client for the app"""
    return TestClientFactory.create_test_client(app)


class ResponsePrototypeExtensionTest(unittest.TestCase):
    """Tests for response prototype extension functionality"""

    def test_response_prototype_extension(self) -> None:
        """Should extend the response prototype"""
        app = express()
        app.response['shout'] = ResponseExtensionFactory.create_shout_uppercase()

        handler = RequestHandlerFactory.create_shout_handler('hey')
        app.use(handler)

        test_client = request(app)
        test_client.get('/').expect('HEY', lambda: None)

    def test_app_specific_extension_isolation(self) -> None:
        """Should only extend for the referenced app"""
        app1 = express()
        app2 = express()

        completion_callback = CallbackCounter(2, lambda: None)

        app1.response['shout'] = ResponseExtensionFactory.create_shout_uppercase()

        app1.get('/', RequestHandlerFactory.create_shout_handler('foo'))
        app2.get('/', RequestHandlerFactory.create_shout_handler('foo'))

        request(app1).get('/').expect(200, 'FOO', completion_callback)
        request(app2).get('/').expect(
            500, 
            re.compile(r'(?:not a function|has no method)'), 
            completion_callback
        )

    def test_sub_app_inheritance(self) -> None:
        """Should inherit to sub apps"""
        parent_app = express()
        sub_app = express()

        completion_callback = CallbackCounter(2, lambda: None)

        parent_app.response['shout'] = ResponseExtensionFactory.create_shout_uppercase()
        parent_app.use('/sub', sub_app)

        parent_app.get('/', RequestHandlerFactory.create_shout_handler('foo'))
        sub_app.get('/', RequestHandlerFactory.create_shout_handler('foo'))

        request(parent_app).get('/').expect(200, 'FOO', completion_callback)
        request(parent_app).get('/sub').expect(200, 'FOO', completion_callback)

    def test_sub_app_method_override(self) -> None:
        """Should allow sub app to override"""
        parent_app = express()
        sub_app = express()

        completion_callback = CallbackCounter(2, lambda: None)

        parent_app.response['shout'] = ResponseExtensionFactory.create_shout_uppercase()
        sub_app.response['shout'] = ResponseExtensionFactory.create_shout_exclamation()
        parent_app.use('/sub', sub_app)

        parent_app.get('/', RequestHandlerFactory.create_shout_handler('foo'))
        sub_app.get('/', RequestHandlerFactory.create_shout_handler('foo'))

        request(parent_app).get('/').expect(200, 'FOO', completion_callback)
        request(parent_app).get('/sub').expect(200, 'foo!', completion_callback)

    def test_parent_app_isolation_from_sub_app(self) -> None:
        """Should not pollute parent app"""
        parent_app = express()
        sub_app = express()

        completion_callback = CallbackCounter(2, lambda: None)

        parent_app.response['shout'] = ResponseExtensionFactory.create_shout_uppercase()
