import os
import posixpath
import sys
from typing import Dict, Any, Optional, Tuple, List

import click


class Repository:
    """Repository configuration and state management."""

    def __init__(self, home: str) -> None:
        self.home = home
        self.config: Dict[str, Any] = {}
        self.verbose = False

    def set_config(self, key: str, value: Any) -> None:
        """Set configuration key-value pair with optional verbose output."""
        self.config[key] = value
        if self.verbose:
            click.echo(f"  config[{key}] = {value}", file=sys.stderr)

    def __repr__(self) -> str:
        return f"<Repo {self.home}>"


class RepositoryOperations:
    """Handles repository operations like clone, delete, etc."""

    @staticmethod
    def clone_repository(repo: Repository, src: str, dest: Optional[str], 
                        shallow: bool, rev: str) -> None:
        """Clone a repository from source to destination."""
        if dest is None:
            dest = posixpath.split(src)[-1] or "."

        click.echo(f"Cloning repo {src} to {os.path.basename(dest)}")
        repo.home = dest

        if shallow:
            click.echo("Making shallow checkout")

        click.echo(f"Checking out revision {rev}")

    @staticmethod
    def delete_repository(repo: Repository) -> None:
        """Delete the current repository."""
        click.echo(f"Destroying repo {repo.home}")
        click.echo("Deleted!")

    @staticmethod
    def set_user_credentials(repo: Repository, username: str, 
                           email: str, password: str) -> None:
        """Set user credentials in repository configuration."""
        repo.set_config("username", username)
        repo.set_config("email", email)
        repo.set_config("password", "*" * len(password))
        click.echo("Changed credentials.")

    @staticmethod
    def commit_changes(repo: Repository, files: Tuple[str, ...], 
                      message: Tuple[str, ...]) -> None:
        """Commit outstanding changes to the repository."""
        commit_message = RepositoryOperations._get_commit_message(files, message)
        if commit_message is None:
            return

        click.echo(f"Files to be committed: {files}")
        click.echo(f"Commit message:\n{commit_message}")

    @staticmethod
    def _get_commit_message(files: Tuple[str, ...], 
                          message: Tuple[str, ...]) -> Optional[str]:
        """Get commit message either from input or editor."""
        if not message:
            return RepositoryOperations._get_message_from_editor(files)
        return "\n".join(message)

    @staticmethod
    def _get_message_from_editor(files: Tuple[str, ...]) -> Optional[str]:
        """Get commit message using editor interface."""
        marker = "# Files to be committed:"
        hint = ["", "", marker, "#"]

        for file in files:
            hint.append(f"#   U {file}")

        edited_message = click.edit("\n".join(hint))
        if edited_message is None:
            click.echo("Aborted!")
            return None

        commit_msg = edited_message.split(marker)[0].rstrip()
        if not commit_msg:
            click.echo("Aborted! Empty commit message")
            return None

        return commit_msg

    @staticmethod
    def copy_files(repo: Repository, src: Tuple[str, ...], 
                  dst: str, force: bool) -> None:
        """Copy files from source to destination."""
        for filename in src:
            click.echo(f"Copy from {filename} -> {dst}")


pass_repo = click.make_pass_decorator(Repository)


@click.group()
@click.option(
    "--repo-home",
    envvar="REPO_HOME",
    default=".repo",
    metavar="PATH",
    help="Changes the repository folder location.",
)
@click.option(
    "--config",
    nargs=2,
    multiple=True,
    metavar="KEY VALUE",
    help="Overrides a config key/value pair.",
)
@click.option("--verbose", "-v", is_flag=True, help="Enables verbose mode.")
@click.version_option("1.0")
@click.pass_context
def cli(ctx: click.Context, repo_home: str, config: List[Tuple[str, str]], 
        verbose: bool) -> None:
    """Repo is a command line tool that showcases how to build complex
    command line interfaces with Click.

    This tool is supposed to look like a distributed version control
    system to show how something like this can be structured.
    """
    # Create a repo object and remember it as as the context object.  From
    # this point onwards other commands can refer to it by using the
    # @pass_repo decorator.
    ctx.obj = Repository(os.path.abspath(repo_home))
    ctx.obj.verbose = verbose
    for key, value in config:
        ctx.obj.set_config(key, value)


@cli.command()
@click.argument("src")
@click.argument("dest", required=False)
@click.option(
    "--shallow/--deep",
    default=False,
    help="Makes a checkout shallow or deep.  Deep by default.",
)
@click.option(
    "--rev", "-r", default="HEAD", help="Clone a specific revision instead of HEAD."
)
@pass_repo
def clone(repo: Repository, src: str, dest: Optional[str], 
          shallow: bool, rev: str) -> None:
    """Clones a repository.

    This will clone the repository at SRC into the folder DEST.  If DEST
    is not provided this will automatically use the last path component
    of SRC and create that folder.
    """
    RepositoryOperations.clone_repository(repo, src, dest, shallow, rev)


@cli.command()
@click.confirmation_option()
@pass_repo
def delete(repo: Repository) -> None:
    """Deletes a repository.

    This will throw away the current repository.
    """
    RepositoryOperations.delete_repository(repo)


@cli.command()
@click.option("--username", prompt=True, help="The developer's shown username.")
@click.option("--email", prompt="E-Mail", help="The developer's email address")
@click.password_option(help="The login password.")
@pass_repo
def setuser(repo: Repository, username: str, email: str, password: str) -> None:
    """Sets the user credentials.

    This will override the current user config.
    """
    RepositoryOperations.set_user_credentials(repo, username, email, password)


@cli.command()
@click.option(
    "--message",
    "-m",
    multiple=True,
    help="The commit message.  If provided multiple times each"
    " argument gets converted into a new line.",
)
@click.argument("files", nargs=-1, type=click.Path())
@pass_repo
def commit(repo: Repository, files: Tuple[str, ...], 
           message: Tuple[str, ...]) -> None:
    """Commits outstanding changes.

    Commit changes to the given files into the repository.  You will need to
    "repo push" to push up your changes to other repositories.

    If a list of files is omitted, all changes reported by "repo status"
    will be committed.
    """
    RepositoryOperations.commit_changes(repo, files, message)


@cli.command(short_help="Copies files.")
@click.option(
    "--force", is_flag=True, help="forcibly copy over an existing managed file"
)
@click.argument("src", nargs=-1, type=click.Path())
@click.argument("dst", type=click.Path())
@pass_repo
def copy(repo: Repository, src: Tuple[str, ...], dst: str, force: bool) -> None:
    """Copies one or multiple files to a new location.  This copies all
    files from SRC to DST.
    """
    RepositoryOperations.copy_files(repo, src, dst, force)

