from typing import Dict, List, Any
from dataclasses import dataclass

@dataclass
class BrowserConfig:
    """Configuration for a browser project."""
    name: str
    browser_name: str
    channel: str = None
    device_config: Dict[str, Any] = None

    def to_dict(self) -> Dict[str, Any]:
        """Convert browser config to playwright project format."""
        use_config = {'browserName': self.browser_name}

        if self.channel:
            use_config['channel'] = self.channel

        if self.device_config:
            use_config.update(self.device_config)

        return {'name': self.name, 'use': use_config}


class PlaywrightConfigBuilder:
    """Builder class for Playwright configuration."""

    def __init__(self):
        self._base_config = {
            'retries': 0,
            'testDir': './test',
            'testMatch': '**/*.spec.js',
            'use': {
                'baseURL': 'http://localhost:9001',
                'headless': True,
            }
        }
        self._browser_configs: List[BrowserConfig] = []

    def add_desktop_browsers(self) -> 'PlaywrightConfigBuilder':
        """Add standard desktop browser configurations."""
        desktop_browsers = [
            BrowserConfig('Chromium', 'chromium'),
            BrowserConfig('Firefox', 'firefox'),
            BrowserConfig('WebKit', 'webkit'),
            BrowserConfig('Microsoft Edge', 'chromium', channel='msedge')
        ]
        self._browser_configs.extend(desktop_browsers)
        return self

    def add_mobile_browsers(self, devices: Dict[str, Any]) -> 'PlaywrightConfigBuilder':
        """Add mobile browser configurations."""
        mobile_browsers = [
            BrowserConfig('Mobile Safari', 'webkit', device_config=devices['iPhone 12']),
            BrowserConfig('Mobile Chrome', 'chromium', device_config=devices['Pixel 5'])
        ]
        self._browser_configs.extend(mobile_browsers)
        return self

    def build(self) -> Dict[str, Any]:
        """Build the final configuration dictionary."""
        config = self._base_config.copy()
        config['projects'] = [browser.to_dict() for browser in self._browser_configs]
