const { devices } = require('@playwright/test');

class BrowserConfig {
    constructor(name, browserName, channel = null, deviceConfig = null) {
        this.name = name;
        this.browserName = browserName;
        this.channel = channel;
        this.deviceConfig = deviceConfig;
    }

    toDict() {
        const useConfig = { browserName: this.browserName };

        if (this.channel) {
            useConfig.channel = this.channel;
        }

        if (this.deviceConfig) {
            Object.assign(useConfig, this.deviceConfig);
        }

        return { name: this.name, use: useConfig };
    }
}

class PlaywrightConfigBuilder {
    constructor() {
        this.baseConfig = {
            retries: 0,
            testDir: './test',
            testMatch: '**/*.spec.js',
            use: {
                baseURL: 'http://localhost:9001',
                headless: true,
            }
        };
        this.browserConfigs = [];
    }

    addDesktopBrowsers() {
        const desktopBrowsers = [
            new BrowserConfig('Chromium', 'chromium'),
            new BrowserConfig('Firefox', 'firefox'),
            new BrowserConfig('WebKit', 'webkit'),
            new BrowserConfig('Microsoft Edge', 'chromium', 'msedge')
        ];
        this.browserConfigs.push(...desktopBrowsers);
        return this;
    }

    addMobileBrowsers(deviceConfigs) {
        const mobileBrowsers = [
            new BrowserConfig('Mobile Safari', 'webkit', null, deviceConfigs['iPhone 12']),
            new BrowserConfig('Mobile Chrome', 'chromium', null, deviceConfigs['Pixel 5'])
        ];
        this.browserConfigs.push(...mobileBrowsers);
        return this;
    }

    build() {
        const config = { ...this.baseConfig };
        config.projects = this.browserConfigs.map(browser => browser.toDict());
        return config;
    }
}

function createPlaywrightConfig(deviceConfigs) {
    return new PlaywrightConfigBuilder()
        .addDesktopBrowsers()
        .addMobileBrowsers(deviceConfigs)
        .build();
}

module.exports = createPlaywrightConfig(devices);

