import click


class ShipService:
    """Service class for ship operations."""

    @staticmethod
    def create_ship(name):
        """Creates a new ship."""
        click.echo(f"Created ship {name}")

    @staticmethod
    def move_ship(ship_name, x_coordinate, y_coordinate, speed_knots):
        """Moves ship to the new location."""
        click.echo(f"Moving ship {ship_name} to {x_coordinate},{y_coordinate} with speed {speed_knots}")

    @staticmethod
    def fire_ship(ship_name, target_x, target_y):
        """Makes ship fire to target coordinates."""
        click.echo(f"Ship {ship_name} fires to {target_x},{target_y}")


class MineService:
    """Service class for mine operations."""

    @staticmethod
    def set_mine(x_coordinate, y_coordinate, mine_type):
        """Sets a mine at specific coordinates."""
        click.echo(f"Set {mine_type} mine at {x_coordinate},{y_coordinate}")

    @staticmethod
    def remove_mine(x_coordinate, y_coordinate):
        """Removes a mine at specific coordinates."""
        click.echo(f"Removed mine at {x_coordinate},{y_coordinate}")


@click.group()
@click.version_option()
def cli():
    """Naval Fate.

    This is the docopt example adopted to Click but with some actual
    commands implemented and not just the empty parsing which really
    is not all that interesting.
    """


@cli.group()
def ship():
    """Manages ships."""


@ship.command("new")
@click.argument("name")
def ship_new(name):
    """Creates a new ship."""
    ShipService.create_ship(name)


@ship.command("move")
@click.argument("ship")
@click.argument("x", type=float)
@click.argument("y", type=float)
@click.option("--speed", metavar="KN", default=10, help="Speed in knots.")
def ship_move(ship, x, y, speed):
    """Moves SHIP to the new location X,Y."""
    ShipService.move_ship(ship, x, y, speed)


@ship.command("shoot")
@click.argument("ship")
@click.argument("x", type=float)
@click.argument("y", type=float)
def ship_shoot(ship, x, y):
    """Makes SHIP fire to X,Y."""
    ShipService.fire_ship(ship, x, y)


@cli.group("mine")
def mine():
    """Manages mines."""


@mine.command("set")
@click.argument("x", type=float)
@click.argument("y", type=float)
@click.option(
    "ty",
    "--moored",
    flag_value="moored",
    default=True,
    help="Moored (anchored) mine. Default.",
)
@click.option("ty", "--drifting", flag_value="drifting", help="Drifting mine.")
def mine_set(x, y, ty):
    """Sets a mine at a specific coordinate."""
    MineService.set_mine(x, y, ty)


@mine.command("remove")
@click.argument("x", type=float)
@click.argument("y", type=float)
def mine_remove(x, y):
    """Removes a mine at a specific coordinate."""
    MineService.remove_mine(x, y)

