import os
import sys
from typing import List, Optional

import click


CONTEXT_SETTINGS = dict(auto_envvar_prefix="COMPLEX")


class Environment:
    """Environment context for CLI operations."""

    def __init__(self) -> None:
        self.verbose: bool = False
        self.home: str = os.getcwd()

    def log(self, msg: str, *args) -> None:
        """Logs a message to stderr."""
        if args:
            msg %= args
        click.echo(msg, file=sys.stderr)

    def vlog(self, msg: str, *args) -> None:
        """Logs a message to stderr only if verbose is enabled."""
        if self.verbose:
            self.log(msg, *args)


class CommandLoader:
    """Handles loading of CLI commands from the commands directory."""

    def __init__(self, commands_folder: str) -> None:
        self._commands_folder = commands_folder

    def list_available_commands(self) -> List[str]:
        """Returns a sorted list of available commands."""
        commands = []
        for filename in os.listdir(self._commands_folder):
            if self._is_command_file(filename):
                command_name = self._extract_command_name(filename)
                commands.append(command_name)
        return sorted(commands)

    def load_command(self, command_name: str) -> Optional[click.Command]:
        """Loads and returns a specific command by name."""
        try:
            module = __import__(f"complex.commands.cmd_{command_name}", None, None, ["cli"])
            return module.cli
        except ImportError:
            return None

    def _is_command_file(self, filename: str) -> bool:
        """Checks if a file is a valid command file."""
        return filename.endswith(".py") and filename.startswith("cmd_")

    def _extract_command_name(self, filename: str) -> str:
        """Extracts command name from filename (removes 'cmd_' prefix and '.py' suffix)."""
        return filename[4:-3]


class ComplexCLI(click.Group):
    """Main CLI group that dynamically loads commands."""

    def __init__(self, *args, **kwargs) -> None:
        super().__init__(*args, **kwargs)
        commands_folder = os.path.abspath(
            os.path.join(os.path.dirname(__file__), "commands")
        )
        self._command_loader = CommandLoader(commands_folder)

    def list_commands(self, ctx: click.Context) -> List[str]:
        """Returns list of available commands."""
        return self._command_loader.list_available_commands()

    def get_command(self, ctx: click.Context, name: str) -> Optional[click.Command]:
        """Returns a specific command by name."""
        return self._command_loader.load_command(name)


pass_environment = click.make_pass_decorator(Environment, ensure=True)


@click.command(cls=ComplexCLI, context_settings=CONTEXT_SETTINGS)
@click.option(
    "--home",
    type=click.Path(exists=True, file_okay=False, resolve_path=True),
    help="Changes the folder to operate on.",
)
@click.option("-v", "--verbose", is_flag=True, help="Enables verbose mode.")
@pass_environment
def cli(ctx: Environment, verbose: bool, home: Optional[str]) -> None:
    """A complex command line interface."""
    ctx.verbose = verbose
    if home is not None:
        ctx.home = home

