from typing import Dict, Any
from abc import ABC, abstractmethod


class ExportFormatter(ABC):
    """Abstract base class for export formatters following Single Responsibility Principle."""

    @abstractmethod
    def get_template(self) -> str:
        """Return the template string for the specific format."""
        pass


class HtmlExportFormatter(ExportFormatter):
    """HTML export formatter responsible for HTML template generation."""

    def get_template(self) -> str:
        """Return the HTML template string."""
        return self._HTML_TEMPLATE

    _HTML_TEMPLATE = """\
<!DOCTYPE html>
<html>
<head>
<meta charset="UTF-8">
<style>
{stylesheet}
body {{
    color: {foreground};
    background-color: {background};
}}
</style>
</head>
<body>
    <pre style="font-family:Menlo,'DejaVu Sans Mono',consolas,'Courier New',monospace"><code style="font-family:inherit">{code}</code></pre>
</body>
</html>
"""


class SvgExportFormatter(ExportFormatter):
    """SVG export formatter responsible for SVG template generation."""

    def get_template(self) -> str:
        """Return the SVG template string."""
        return self._SVG_TEMPLATE

    _SVG_TEMPLATE = """\
<svg class="rich-terminal" viewBox="0 0 {width} {height}" xmlns="http://www.w3.org/2000/svg">
    <!-- Generated with Rich https://www.textualize.io -->
    <style>

    @font-face {{
        font-family: "Fira Code";
        src: local("FiraCode-Regular"),
                url("https://cdnjs.cloudflare.com/ajax/libs/firacode/6.2.0/woff2/FiraCode-Regular.woff2") format("woff2"),
                url("https://cdnjs.cloudflare.com/ajax/libs/firacode/6.2.0/woff/FiraCode-Regular.woff") format("woff");
        font-style: normal;
        font-weight: 400;
    }}
    @font-face {{
        font-family: "Fira Code";
        src: local("FiraCode-Bold"),
                url("https://cdnjs.cloudflare.com/ajax/libs/firacode/6.2.0/woff2/FiraCode-Bold.woff2") format("woff2"),
                url("https://cdnjs.cloudflare.com/ajax/libs/firacode/6.2.0/woff/FiraCode-Bold.woff") format("woff");
        font-style: bold;
        font-weight: 700;
    }}

    .{unique_id}-matrix {{
        font-family: Fira Code, monospace;
        font-size: {char_height}px;
        line-height: {line_height}px;
        font-variant-east-asian: full-width;
    }}

    .{unique_id}-title {{
        font-size: 18px;
        font-weight: bold;
        font-family: arial;
    }}

    {styles}
    </style>

    <defs>
    <clipPath id="{unique_id}-clip-terminal">
      <rect x="0" y="0" width="{terminal_width}" height="{terminal_height}" />
    </clipPath>
    {lines}
    </defs>

    {chrome}
    <g transform="translate({terminal_x}, {terminal_y})" clip-path="url(#{unique_id}-clip-terminal)">
    {backgrounds}
    <g class="{unique_id}-matrix">
    {matrix}
    </g>
    </g>
</svg>
"""


class ExportFormatConfig:
    """Configuration class for export format constants."""

    SVG_FONT_FAMILY: str = "Rich Fira Code"
    SVG_CLASSES_PREFIX: str = "rich-svg"


class ExportFormatFactory:
    """Factory class for creating export formatters following Open/Closed Principle."""

    _formatters: Dict[str, type] = {
        'html': HtmlExportFormatter,
        'svg': SvgExportFormatter,
    }

    @classmethod
    def create_formatter(cls, format_type: str) -> ExportFormatter:
        """Create and return an export formatter instance."""
        if format_type not in cls._formatters:
            raise ValueError(f"Unsupported format type: {format_type}")
        return cls._formatters[format_type]()

    @classmethod
    def register_formatter(cls, format_type: str, formatter_class: type) -> None:
        """Register a new formatter class for extension."""
        cls._formatters[format_type] = formatter_class


# Backward compatibility - maintain the same public interface
def _get_console_html_format() -> str:
    """Get HTML format template for backward compatibility."""
    formatter = ExportFormatFactory.create_formatter('html')
    return formatter.get_template()


def _get_console_svg_format() -> str:
    """Get SVG format template for backward compatibility."""
    formatter = ExportFormatFactory.create_formatter('svg')
    return formatter.get_template()


# Maintain exact same functionality with original variable names
CONSOLE_HTML_FORMAT = _get_console_html_format()
CONSOLE_SVG_FORMAT = _get_console_svg_format()
_SVG_FONT_FAMILY = ExportFormatConfig.SVG_FONT_FAMILY
_SVG_CLASSES_PREFIX = ExportFormatConfig.SVG_CLASSES_PREFIX

