import os
import posixpath
import sys
from typing import Dict, Any, Optional, Tuple, List

import click


class Repository:
    """Repository configuration and state management."""

    def __init__(self, home: str) -> None:
        self.home = home
        self.config: Dict[str, Any] = {}
        self.verbose = False

    def set_config(self, key: str, value: Any) -> None:
        """Set configuration key-value pair with optional verbose output."""
        self.config[key] = value
        if self.verbose:
            click.echo(f"  config[{key}] = {value}", file=sys.stderr)

    def __repr__(self) -> str:
        return f"<Repo {self.home}>"


class RepositoryOperations:
    """Handles repository operations like clone, delete, etc."""

    @staticmethod
    def clone_repository(repo: Repository, src: str, dest: Optional[str], 
                        shallow: bool, rev: str) -> None:
        """Clone a repository from source to destination."""
        if dest is None:
            dest = posixpath.split(src)[-1] or "."

        click.echo(f"Cloning repo {src} to {os.path.basename(dest)}")
        repo.home = dest

        if shallow:
            click.echo("Making shallow checkout")

        click.echo(f"Checking out revision {rev}")

    @staticmethod
    def delete_repository(repo: Repository) -> None:
        """Delete the current repository."""
        click.echo(f"Destroying repo {repo.home}")
        click.echo("Deleted!")

    @staticmethod
    def set_user_credentials(repo: Repository, username: str, 
                           email: str, password: str) -> None:
        """Set user credentials in repository configuration."""
        repo.set_config("username", username)
        repo.set_config("email", email)
        # Don't store the actual password in config
        click.echo("Changed credentials.")

    @staticmethod
    def commit_changes(repo: Repository, files: Tuple[str, ...], 
                      message: Tuple[str, ...]) -> None:
        """Commit outstanding changes to the repository."""
        commit_message = RepositoryOperations._get_commit_message(files, message)
        if commit_message is None:
            return

        click.echo(f"Files to be committed: {files}")
        click.echo(f"Commit message:\n{commit_message}")

    @staticmethod
    def _get_commit_message(files: Tuple[str, ...], 
                          message: Tuple[str, ...]) -> Optional[str]:
        """Get commit message either from input or editor."""
        if not message:
            return RepositoryOperations._get_message_from_editor(files)
        return "\n".join(message)

    @staticmethod
    def _get_message_from_editor(files: Tuple[str, ...]) -> Optional[str]:
        """Get commit message using editor interface."""
        marker = "# Files to be committed:"
        hint = ["", "", marker, "#"]

        for file in files:
            hint.append(f"#   U {file}")

        edited_message = click.edit("\n".join(hint))
        if edited_message is None:
            click.echo("Aborted!")
            return None

        commit_msg = edited_message.split(marker)[0].rstrip()
        if not commit_msg:
            click.echo("Aborted! Empty commit message")
            return None

        return commit_msg

    @staticmethod
    def copy_files(repo: Repository, src: Tuple[str, ...], 
                  dst: str, force: bool) -> None:
        """Copy files from source to destination."""
        for filename in src:
            click.echo(f"Copy from {filename} -> {dst}")


