@click.password_option(help="The login password.")
@pass_repo
def setuser(repo: Repository, username: str, email: str, password: str) -> None:
    """Sets the user credentials.

    This will override the current user config.
    """
    RepositoryOperations.set_user_credentials(repo, username, email, password)


