    app: Any = None  # This would be imported from the actual app module

    def describe_get_users() -> None:
        def it_should_respond_with_users() -> None:
            def done(error: Any = None) -> None:
                if error:
                    raise error

            response: requests.Response = requests.get(f"{app.base_url}/users")
            assert response.text == 'user list'
            done()

    def describe_delete_users() -> None:
        def it_should_delete_users() -> None:
            def done(error: Any = None) -> None:
                if error:
                    raise error

            response: requests.Response = requests.delete(f"{app.base_url}/users")
            assert response.text == 'delete users'
            done()

    def describe_get_users_by_id() -> None:
        def it_should_get_a_user() -> None:
            def done(error: Any = None) -> None:
                if error:
                    raise error

            response: requests.Response = requests.get(f"{app.base_url}/users/12")
            assert response.text == 'user 12'
            done()

    def describe_get_users_pets() -> None:
        def it_should_get_a_users_pets() -> None:
            def done(error: Any = None) -> None:
                if error:
                    raise error

            response: requests.Response = requests.get(f"{app.base_url}/users/12/pets")
            assert response.text == "user 12's pets"
            done()

    def describe_get_users_pet_by_id() -> None:
        def it_should_get_a_users_pet() -> None:
            def done(error: Any = None) -> None:
                if error:
                    raise error

            response: requests.Response = requests.delete(f"{app.base_url}/users/12/pets/2")
            assert response.text == "delete 12's pet 2"
            done()

