describe('Route map', function(){
  var app;

  before(function(){
    app = require('../fixtures/route-map');
  });

  describe('GET /users', function(){
    it('should respond with users', function(done){
      request(app)
      .get('/users')
      .expect('user list', done);
    })
  })

  describe('DELETE /users', function(){
    it('should delete users', function(done){
      request(app)
      .delete('/users')
      .expect('delete users', done);
    })
  })

  describe('GET /users/:id', function(){
    it('should get a user', function(done){
      request(app)
      .get('/users/12')
      .expect('user 12', done);
    })
  })

  describe('GET /users/:id/pets', function(){
    it('should get a users pets', function(done){
      request(app)
      .get('/users/12/pets')
      .expect("user 12's pets", done);
    })
  })

  describe('DELETE /users/:id/pets/:pid', function(){
    it('should delete a users pet', function(done){
      request(app)
      .delete('/users/12/pets/2')
      .expect("delete 12's pet 2", done);
    })
  })
})

